mapper.select:
SELECT id, content, emoji_id, login_id, create_date
FROM diary
WHERE login_id = #{loginId} AND create_date = #{createDate}
ORDER BY create_date desc;

SELECT id, content, emoji_id, login_id, create_date
FROM diary
WHERE login_id = #{loginId} AND id = #{diaryId}

mapper.update:
UPDATE diary
SET create_date = #{createDate}, emoji_id = #{emojiId}, content = #{content}
WHERE id = #{id} AND login_id = #{loginId}
